<?phpif (!$_POST['arealocation']) {    go($setup['adm']['www'].'map/map.html?newarea=1');}if ($_POST['submitValue'] == 'new') {    if (!$_SESSION['filter']['company']){        $err[] = _("SELECTYOURCOMPANY");    }    if (!$_POST['areaname']) {        $err[] = _("YOUDONTFILLAREANAME");    }    if (!$_POST['arealocation']) {        $err[] = _("YOUDONTFILLAREALOCATION");    }    if (!$err) {      if (!query("INSERT INTO area (`area_name`, `location`, `company_ID_company`) VALUES ('".$_POST['areaname']."','".$_POST['arealocation']."',".$_SESSION['filter']['company'].");")){          $db_err[] = "Chyba při ukládání uživatele.";          $smarty->assign("db_err", $db_err);       }else{            $list_sql = query("SELECT ID_area FROM area WHERE company_ID_company=".$_SESSION['filter']['company']." ORDER BY ID_area DESC  limit 1;");            $id = fetch_array($list_sql);            $id = $id[0]['ID_area'];            go($setup['adm']['www'].'areas/edit.html?area='.$id);        }    } else {        $smarty->assign("err", $err);    }}?>