<?phpif(isset($_GET['company'])){    $_POST['company']=$_GET['company'];}if ($_POST['submitValue'] == 'edit') {    if (!$_POST['company_name']) {        $err[] = _("YOUDONTFILLCOMPANYNAME");    }    if (!$err) {        if (!query("UPDATE company SET `company_name`='".$_POST['company_name']."' WHERE ID_company='".$_POST['company']."';")){            $db_err[] = "DBUPDATEERROR";            $smarty->assign("dbquary", "UPDATE company SET `company_name`='".$_POST['company_name']."' WHERE ID_company='".$_POST['company']."';");            $smarty->assign("err", $db_err);        }else{            go($setup['adm']['www'].'companies/list.html');        }    } else {        $smarty->assign("err", $err);    }}elseif ($_POST['submitValue'] == 'delete') {    $list_sql = query("SELECT COUNT(*) AS rows FROM area WHERE company_ID_company='".$_POST['company']."';");    if (!$list_sql){        $db_err[] = "DBSELECTAREAERROR";        $smarty->assign("err", $db_err);    }else {        $result = fetch_array($list_sql);        $rows = $result[0];        $rows = $rows['rows'];    if($rows <= 0){        if (!query("DELETE FROM company WHERE ID_company='" . $_POST['company'] . "';")) {            $db_err[] = "DBDELETEERROR";            $smarty->assign("err", $db_err);        } else {            go($setup['adm']['www'] . 'companies/list.html');        }    }else{        $err[] = "COMPANYHAVEAREAS";        $smarty->assign("err", $err);    }    }}else{        $list_sql = query("SELECT ID_company,company_name FROM company WHERE ID_company=".$_POST['company'].";");        $result = fetch_array($list_sql);        $company = $result[0];        $_POST['company_name'] = $company['company_name'];}$smarty->assign('company',$_POST['company']);$smarty->assign('company_name',$_POST['company_name']);?>