<?php$option['modul'] = fetch_array(query("SELECT `id`, `nazev` FROM `modul` WHERE `druh`='main' ORDER BY `poradi`"));$smarty->assign("option", $option);if ($_POST['submitValue'] == 'new') {    if ($_POST['def']['nazev'] == '' OR $_POST['def']['page'] == '') {        $err[] = "Musíte vyplnit všechny povinné položky.";    }    if ($err) {        $smarty->assign("err", $err);    } else {        $inp = poster($_POST['def'], "db_set");        $modul_id = intval($_POST['modul_id']);        if ($modul_id) {            $inp['db_set'][] = "`modul_id`='{$modul_id}'";            $inp['db_set'][] = "`druh`='sub'";            $mainModul = fetch_assoc(query("SELECT `page` FROM `modul` WHERE `id`='{$modul_id}'"));            $inp['db_set'][] = "`url`='{$mainModul['page']}/{$_POST['def']['page']}.html'";        }        if (query("INSERT INTO `modul` SET " . implode(",", $inp['db_set']))) {            go($setup['adm']['www'] . "nastaveni/seznamModul.html");        } else {            $smarty->assign("err", "Při ukládání se vyskytla chyba.");        }    }}?>