<?php$include['js'][] = "./nastaveni/js-seznam.tpl";$smarty->assign("include", $include);$list_sql = query("SELECT `id`, `nazev`, `page`, `icon`, `zobraz` FROM `modul` WHERE `druh`='main' ORDER BY `poradi`");while ($it = fetch_assoc($list_sql)) {    $list[$it['id']] = $it;    $submodul_sql = query("SELECT `id`, `nazev`, `page`, `icon`, `url`, `zobraz` FROM `modul` WHERE `modul_id`='{$it['id']}' ORDER BY `poradi`");    while ($item = fetch_assoc($submodul_sql)) {        $list[$it['id']]['submodul'][$item['id']] = $item;        $listPage[$item['id']] = $item['page'];    }    $listPage[$it['id']] = $it['page'];}$smarty->assign("list", $list);$smarty->assign("listPage", $listPage);//trizeni polozekif ($_POST['function'] == 'sort') {    $data = json_decode($_POST['data'], true);    if ($data) {        foreach ($data AS $key => $value) {            $value['id'] = intval($value['id']);            query("UPDATE `modul` SET `poradi`='{$key}', `druh`='main', `url`='', `modul_id`= NULL WHERE `id`='{$value['id']}'");            if ($value['children']) {                foreach ($value['children'] AS $k => $v) {                    $v['id'] = intval($v['id']);                    query("UPDATE `modul` SET `poradi`='{$k}', `druh`='sub', `url`='{$listPage[$value['id']]}/{$listPage[$v['id']]}.html', `modul_id`='{$value['id']}' WHERE `id`='{$v['id']}'");                }            }        }        die('ok');    } else {        die('chyba');    }}?>